import React from 'react';
import Courses from '../data/CourseDetails.json';
<link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet" />

function courses() {
  return (
    <div name='work' className='h-screen bg-[#2a9d8f]'>
    <div>
    <h1>Course Curator</h1>

          {
            Courses && Courses.map( course => {
              if (course.stars !== null){

              
              
              return(
                <div className="card" key={ course.id }>
                  <div className="background">
                    <strong>{ course.title }</strong><br></br>
                    <span id="stars"></span><br></br>
                    <p>Time: { course.time }</p><br></br>
                    
                  </div>
                  <div className="foreground">
                    <h1>Points: 50</h1>
                  </div>
                </div>
              
                

              )
              }
              else{
                return(
                  <div className="card" key={ course.id }>
                  <div className="background">
                    <strong>{ course.title }</strong><br></br>
                    <p>Time: { course.time }</p><br></br>
                  </div>
                  <div className="foreground">
                    <h1>Points: 50</h1>
                  </div>
                </div>
                )
              }
              
              
            })
          }
        </div>
      </div>
  );
}

export default courses;
