import React, { useState } from "react";
import Courses from '../data/CourseDetails.json';
import { AiFillCloseCircle } from "react-icons/ai";
import Buttons from "./buttons";
import CourseCard from "./CourseCard";

const Product = () => {
        const [detail, setDetail] = useState([]);
        const [close, setClose] = useState(false);
        const [item, setItem] = useState(Courses);
        const menuItems = [...new Set(Courses.map((Val) => Val.category))];

        const filterItem = (curcat) => {
          const newItem = Courses.filter((newVal) => {
            return newVal.category === curcat;
          });
          setItem(newItem);
        };

        const detailPage = (Product) =>
        {
            setDetail([{...Product}])
            setClose(true)
        }
    return(

        <div className="App">
        {
           
            close ?
            <div className="detail_container">
            <div className="detail_content">
            <button className="close" onClick={() => setClose(false)}><AiFillCloseCircle/></button>
            {
                detail.map((x) =>
                {                   
                  if (x.stars !== null){
                    return(
                      <div className="detail_info">
                        <div className="img-box">
                          <img src="" alt={x.title}></img>
                        </div>
                        <div className="course_detail">
                          <h2>{x.title}</h2>
                          <h3>{x.time}</h3>
                          <h3>{x.stars}</h3>
                        </div>

                      </div>
                  )   
                  }
                  else{
                    return(
                      <div className="detail_info">
                        <div className="img-box">
                          <img src="" alt={x.title}></img>
                        </div>
                        <div className="course_detail">
                          <h2>{x.title}</h2>
                          <p>{x.time}</p>
                        </div>
                      </div>
                  )   
                  }   
                })
            }
            </div>

        </div>:null
        }

    <div className="App">
      <Buttons
          filterItem={filterItem}
          setItem={setItem}
          menuItems={menuItems}
      />
    {
        <CourseCard item={item} />      
    }
    </div>
    </div>
       
    )

}

export default Product;
