import { Link } from "react-router-dom"
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../store/authSlice";
import axios from "axios";

//Nav bar for each page

export default function Navbar() {
  const loggedIn = useSelector((state) => state.auth.isLoggedIn)
  const user = useSelector((state) => state.auth.user)
  const dispatch = useDispatch()
  if(loggedIn){
    useEffect(() => {
      axios.get('http://localhost:8080/profile', {params: {user}})
      .then(res => {
        let points = res.data
        setPoints(points)
        setError(null)
      }).catch(err => setError("couldnt fetch"))
    }, [user])
  }
  //Navbar will change depending on if user is logged in or not 
 
    return (
      <nav className="nav">
        <Link to="/" className="site-title">
          Course Curator
        </Link>
        { loggedIn ?
        <ul className="nav__list">
          <li className="nav__item">
            <Link to={"/"}>Home</Link>
          </li>
          <li className="nav__item">
            <Link to={"/courses"}>Courses</Link>
          </li>
          <li className="nav__item">
            <Link to={"/about"}>About</Link>
          </li>
          <li className="nav__item">
            <Link to={"/questionnaire"}>Questionnaire</Link>
          </li>
          <li className="nav__item"><Link to='/profile'>Profile</Link></li>
          <li className="nav__item"><Link to='/' onClick={() => dispatch(logout())}>Logout</Link></li>
        </ul>
        :
        <ul className="nav__list">
        <li className="nav__item">
          <Link to={"/"}>Home</Link>
        </li>
        <li className="nav__item">
          <Link to={"/courses"}>Courses</Link>
        </li>
        <li className="nav__item">
          <Link to={"/about"}>About</Link>
        </li>
        <li className="nav__item">
          <Link to={"/questionnaire"}>Questionnaire</Link>
        </li>
        <li className="nav__item"><Link to='/signup'>Sign Up</Link></li>
        <li className="nav__item"><Link to='/login'>Sign In</Link></li>


        </ul>
      }
      </nav>
    )
};
