import React from "react";
import Courses from '../data/CourseDetails.json';

const Product = () => {
    return(
        <div className="App">
        {
            Courses.map((curElm) =>
            {
                if (course.stars !== null){
                    return(
                        <div className="card" key={ curElm.id }>
                          <div className="background">
                            <strong>{ curElm.title }</strong><br></br>
                            <span id="stars"></span><br></br>
                            <p>Time: { curElm.time }</p><br></br>
                            
                          </div>
                          <div className="foreground">
                            <h1>Points: 50</h1>
                            <button onClick={() => courseDetail(curElm)}>View</button>      
                          </div>
                        </div>
                      )
                }
                else{
                        return(
                            <div className="card" key={ curElm.id }>
                              <div className="background">
                                <strong>{ curElm.title }</strong><br></br>
                                <p>Time: { curElm.time }</p><br></br>
                                
                              </div>
                              <div className="foreground">
                                <h1>Points: 50</h1>
                                <button onClick={() => courseDetail(curElm)}>View</button>      
                              </div>
                            </div>
                          ) 
                }
            })
        }
        </div>
    )

}

export default Product;
