  const isLoggedIn = await getIsLoggedIn();
  return {
    isLoggedIn,
    needsSecondFactor: isFirstFactorTokenPresent(),
    firstFactors: authenticationData.firstFactors,
    secondFactors: authenticationData.secondFactors,
    resetMfaState: clearMfa,
  };
}
