    return;
  }
  try {
    document && window;
  } catch (error) {
    return;
  }

  // Perform hard redirect
  const el = document.createElement("a");
  el.href = pathOrUrl;
  let path = `${el.pathname}${el.hash}${el.search}`;
  if (el.pathname !== window.location.pathname) {
    window.location.assign(path);
  }
}
